      message_body: function message_body(data, pending, reply, nolink) {
        if (pending === true) {
          switch (data.type) {
            case 1:
            case 2:
            case 3:
            case 4:
              return '<div class="h3 spinner-grow text-danger" style="width: 4rem; height: 4rem;" role="status">\n' + '  <span class="sr-only">Uploading...</span>\n' + '</div>';
            default:
              return Messenger.format().shortcodeToImage(data.body);
          }
        }
        if (reply === true) {
          switch (data.type) {
            case 1:
              return nolink === true ? '<img height="100" src="' + data.image.md + '" />' : '<a target="_blank" href="' + data.image.lg + '"><img height="100" class="msg_image NS" src="' + data.image.md + '" /></a>';
            case 2:
              return nolink === true ? '<i class="fas fa-file-download"></i> ' + data.body : '<a href="' + data.document + '" target="_blank"><i class="fas fa-file-download"></i> ' + data.body + '</a>';
            case 3:
              return nolink === true ? '<i class="fas fa-music"></i> ' + data.body : '<a href="' + data.audio + '" target="_blank"><i class="fas fa-music"></i> ' + data.body + '</a>';
            case 4:
              return nolink === true ? '<i class="fas fa-video"></i> ' + data.body : '<a href="' + data.video + '" target="_blank"><i class="fas fa-video"></i> ' + data.body + '</a>';
            default:
              return methods.format_message_body(data.body, true);
          }
        }
        switch (data.type) {
          case 1:
            return '<a target="_blank" href="' + data.image.lg + '">' + '<img class="msg_image NS img-fluid" src="' + data.image.md + '" />' + '<div class="h3 spinner-grow text-info" style="width: 4rem; height: 4rem;" role="status"><span class="sr-only">loading...</span></div>' + '</a>';
          case 2:
            return '<a href="' + data.document + '" target="_blank"><i class="fas fa-file-download"></i> ' + data.body + '</a>';
          case 3:
            var audio = '<audio controls preload="none" class="message-audio-player"><source src="' + data.audio + '?stream=true"></audio>';
            if (data.extra !== null && data.extra.audio_message) {
              return '<a href="' + data.audio + '" target="_blank"><i class="fas fa-volume-up"></i> Audio Message</a><hr>' + audio;
            }
            return '<a href="' + data.audio + '" target="_blank"><i class="fas fa-volume-up"></i> ' + data.body + '</a><hr>' + audio;
          case 4:
            var video = '<div class="embed-responsive embed-responsive-16by9 my-2"><video class="embed-responsive-item" controls preload="metadata"><source src="' + data.video + '?stream=true"></video></div>';
            return '<a href="' + data.video + '" target="_blank"><i class="fas fa-video"></i> ' + data.body + '</a><hr>' + video;
          default:
            var body = methods.format_message_body(data.body);
            return data.edited ? body + ' <span onclick="ThreadManager.load().messageEdits(\'' + data.edited_history_route + '\')" class="pointer_area"><small class="font-weight-bold text-muted" title="Edited on ' + Messenger.format().makeHumanTime(data.updated_at) + '">(edited)</small></span>' : body;
        }
      },
