      message_reactions: function message_reactions(message, mine, grouped) {
        if (message.reacted) {
          var html = '<div class="clearfix"></div><div class="message-reactions ' + (grouped ? "" : "grouped-reactions") + ' ' + (mine ? "my-reactions" : "") + '"><div class="reactions-body px-1">';
          for (var reaction in message.reactions.data) {
            if (message.reactions.data.hasOwnProperty(reaction)) {
              var reactedByMe = message.reactions.data[reaction].find(function (reactors) {
                return Messenger.isProvider(reactors.owner_id, reactors.owner_type);
              });
              var names = '';
              var reactionCount = message.reactions.data[reaction].length;
              for (var y = 0; y < reactionCount; y++) {
                if (y === 0) {
                  names += message.reactions.data[reaction][y].owner.name;
                } else if (y === 1 && reactionCount === 2) {
                  names += ' and ' + message.reactions.data[reaction][y].owner.name;
                } else if (y === 2) {
                  if (reactionCount === 3) {
                    names += ' and ' + message.reactions.data[reaction][y].owner.name;
                  } else {
                    names += ', ' + message.reactions.data[reaction][y].owner.name + ' and ' + (reactionCount - 3) + ' others';
                  }
                  break;
                } else {
                  names += ', ' + message.reactions.data[reaction][y].owner.name;
                }
              }
              names += ' reacted with ' + reaction;
              var tooltip = Messenger.format().escapeHtml('<div class="row my-2 mr-2"><div class="col-3">' + Messenger.format().shortcodeToImage(reaction) + '</div><div class="col-9">' + names + '</div></div>');
              if (reactedByMe) {
                html += '<span data-toggle="tooltip" data-html="true" title="' + tooltip + '" data-placement="top" onclick="ThreadManager.removeReaction({message_id : \'' + message.id + '\', id : \'' + reactedByMe.id + '\'})" ' + ' class="reaction-badge reacted-by-me badge badge-light px-1 pointer_area">' + methods.format_message_body(reaction, true) + '<span class="ml-1 font-weight-bold text-primary">' + message.reactions.data[reaction].length + '</span></span>';
              } else {
                html += '<span data-toggle="tooltip" data-html="true" title="' + tooltip + '" data-placement="top" onclick="ThreadManager.addNewReaction({message_id : \'' + message.id + '\', emoji : \'' + reaction + '\'})"' + ' class="reaction-badge badge badge-light px-1 pointer_area">' + methods.format_message_body(reaction, true) + '<span class="ml-1 font-weight-bold">' + message.reactions.data[reaction].length + '</span></span>';
              }
            }
          }
          return html + '</div></div>';
        }
        return '';
      },
